import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BarChart} from 'react-d3-components';
import Slider from "react-slick";
import * as d3 from "d3";
import {Bar} from 'react-chartjs-2';
//var ReactCSSTransitionGroup = require('react-addons-css-transition-group');


// Here we init the variables
let year = 0;
//This is the style var we are using
let yearStyle;
//This was being used to hold state data from the chart, but i'm not using it now, it's left in incase you want to use it
let stateData;
let statePosition;
// This is false by default i.e chart wont play auto
let playChart = true;


const data_1 = {
  labels: ['2013-2014', '2014-2015', '2015-2016','2016-2017'],
  datasets: [
    {
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-5.7],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [1.4],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [6.8],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'GDP Growth Rate (World)',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [2.9],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [39],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
  ]
}

const sngl_bar = value => {
   return 'rgb(68, 114, 196)';
};

class India extends React.Component {
    constructor(props) {
        super(props);
        this.play = this.play.bind(this);
        this.pause = this.pause.bind(this);
        this.state = { width: window.innerWidth, chartData: data_1}; // responsive mobile

      }

      handlePress(){
        console.log("From Child")
      }

      play() {
        console.log("Child PLay")
        //Just using componentdid mount && a boolean instead of using an export function to trigger animation
        playChart = true;

        //this.componentDidMount();
        this.slider.slickPlay();
      }
      pause() {
        this.saveState();
        playChart = false;
        this.slider.slickPause();
      }
    // responsive mobile Start
    componentWillMount() {
      window.addEventListener('resize', this.handleWindowSizeChange);
    }

    // make sure to remove the listener
    // when the component is not mounted anymore
    componentWillUnmount() {
      window.removeEventListener('resize', this.handleWindowSizeChange);
    }

    handleWindowSizeChange = () => {
      this.setState({ width: window.innerWidth });
    };

    animation(data, datasetPosition) {
      const oldState = this.state.chartData;

      let datasets = oldState.datasets;
      datasets[datasetPosition] = data;
      const newState = Object.assign({}, oldState, {
        datasets: datasets
      })

      this.setState({
        chartData: newState
      })
    }

    updateColumn(data, datasetPosition) {
      const oldState = this.state.chartData;

      let datasets = oldState.datasets;
      datasets[datasetPosition] = data;
      const newState = Object.assign({}, oldState, {
        datasets: datasets
      })

      this.setState({
        chartData: newState
      })
  }

  componentDidMount() {
    setTimeout(() => this.animation({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-5.7],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 500)

    setTimeout(() => this.animation({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [-1.4],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 1000)
        //
    setTimeout(() => this.animation({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      // data: [1, 0, -0.5, 1.7],
      data: [6.8],
      backgroundColor:[
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE'
      ],
    }, 2), 1500)

    setTimeout(() => this.animation({
      label: 'GDP Growth Rate (world)',
      backgroundColor: '#DCDCDC',
      borderColor: '#DCDCDC',
      borderWidth: 1,
      hoverBackgroundColor: '#DCDCDC',
      hoverBorderColor: '#DCDCDC',
      // data: [2.9, 2.9, 2.5, 3.2],
      data: [2.9],
      backgroundColor:[
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC'
      ],
    }, 3), 2000)

    setTimeout(() => this.animation({
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#4472c4',
      borderColor: '#4472c4',
      borderWidth: 1,
      hoverBackgroundColor: '#4472c4',
      hoverBorderColor: '#4472c4',
      // data: [6.2, 14.9, 32.8, 36.9],
      data: [39],
      backgroundColor:[
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4'
      ],
    }, 4), 2500)


    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-5.7, -16.7],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 3000)

    setTimeout(() => this.updateColumn({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [1.4, -2.9],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 3500)
        //
    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      data: [6.8, 6.5],
      backgroundColor:[
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE'
      ],
    }, 2), 4500)

    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (world)',
      backgroundColor: '#DCDCDC',
      borderColor: '#DCDCDC',
      borderWidth: 1,
      hoverBackgroundColor: '#DCDCDC',
      hoverBorderColor: '#DCDCDC',
      data: [2.9, 2.9],
      backgroundColor:[
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC'
      ],
    }, 3), 5000)

    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#4472c4',
      borderColor: '#4472c4',
      borderWidth: 1,
      hoverBackgroundColor: '#4472c4',
      hoverBorderColor: '#4472c4',
      data: [39, 34.5],
      backgroundColor:[
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4'
      ],
    }, 4), 5500)

    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-5.7, -16.7, -1.5],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 6000)


    setTimeout(() => this.updateColumn({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [1.4, -2.9, 2.3],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 6500)
        //
    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      data: [6.8, 6.5, 6.3],
      backgroundColor:[
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE'
      ],
    }, 2), 7000)

    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (world)',
      backgroundColor: '#DCDCDC',
      borderColor: '#DCDCDC',
      borderWidth: 1,
      hoverBackgroundColor: '#DCDCDC',
      hoverBorderColor: '#DCDCDC',
      data: [2.9,2.9,2.5],
      backgroundColor:[
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC'
      ],
    }, 3), 7500)

    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#4472c4',
      borderColor: '#4472c4',
      borderWidth: 1,
      hoverBackgroundColor: '#4472c4',
      hoverBorderColor: '#4472c4',
      data: [39, 34.5, 41.2],
      backgroundColor:[
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4'
      ],
    }, 4), 8500)

    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-5.7, -16.7, -1.5, 13.6],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 9000)


    setTimeout(() => this.updateColumn({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [1.4, -2.9, 2.3, 10.9],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 9500)
        //
    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      data:[6.8, 6.5, 6.3, 6.6],
      backgroundColor:[
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE'
      ],
    }, 2), 10000)

    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (world)',
      backgroundColor: '#DCDCDC',
      borderColor: '#DCDCDC',
      borderWidth: 1,
      hoverBackgroundColor: '#DCDCDC',
      hoverBorderColor: '#DCDCDC',
      data: [2.9, 2.9, 2.5, 3.2],
      backgroundColor:[
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC'
      ],
    }, 3), 10500)

    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#4472c4',
      borderColor: '#4472c4',
      borderWidth: 1,
      hoverBackgroundColor: '#4472c4',
      hoverBorderColor: '#4472c4',
      data: [39, 34.5, 41.2, 31.4],
      backgroundColor:[
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4'
      ],
    }, 4), 11000)

}

// responsive mobile end...
    render(){
        const settings = {
            dots: true,
            infinite: true,
            slidesToShow: 1,
            slidesToScroll: 1,
            autoplay: false,
            arrows:true
            // autoplaySpeed: 5000
          };
          const { width } = this.state; // responsive mobile
		      const isMobile = width <= 500; // responsive mobile
          if (isMobile) {
            return(
              <div>

                      <Slider ref={slider => (this.slider = slider)} {...settings}>
                          <div className="br_sldr_upr">
                            <h2>Growth rate comparison</h2>
                            <div className="bar_graph bar_sldr">
                              {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                              <Bar
                              data={data_1}
                              width={320}
                              height={400}
                              margin={{top: 10, bottom: 50, left: 50, right: 10}}
                              options={{
                                legend: {
                                  display: false
                                },
                                animation:{
                                  duration:10000
                                },
                                scales: {
                                  yAxes: [{
                                    ticks: {
                                      beginAtZero:true,
                                      callback: function(value, index, values) {
                                        return (value*1)+'%';
                                      },
                                    },
                                  }]
                                },
                              }}
                            />
                            </div>
                          </div>
                      </Slider>

                      <div className="ply_pause_btn" style={{ textAlign: "center" }}>
                        <div className="both_ply_pause">
                          <button className="button" onClick={this.play}>
                            <img src="image/play_button.png" />
                          </button>
                          <button className="button pause_btn" onClick={this.pause}>
                            <img src="image/pause_button.png" />
                          </button>
                        </div>
                      </div>
                    </div>
            );
          }

        else{
          return(
            <div>

                    <Slider ref={slider => (this.slider = slider)} {...settings}>
                        <div className="br_sldr_upr">
                          <h2>Growth rate comparison</h2>
                          <div className="bar_graph bar_sldr">
                            {/* <h2 style={{textAlign: 'center'}}>Change in number of trade partners between 2013 and 2017</h2> */}
                            <Bar
                              data={data_1}
                              width={680}
                              height={400}
                              margin={{top: 10, bottom: 50, left: 50, right: 10}}
                              options={{
                                legend: {
                                  display: false
                                },
                                animation:{
                                  duration:10000
                                },
                                scales: {
                                  yAxes: [{
                                    ticks: {
                                      beginAtZero:true,
                                      callback: function(value, index, values) {
                                        return (value*1)+'%';
                                      },
                                    },
                                  }]
                                },
                              }}
                            />
                          </div>
                        </div>
                    </Slider>

                    <div className="ply_pause_btn" style={{ textAlign: "center" }}>
                      <div className="both_ply_pause">
                        <button className="button" onClick={this.props.handlePress}>
                          <img src="image/play_button.png" />
                        </button>
                        <button className="button pause_btn" onClick={this.props.handleReset}>
                          <img src="image/pause_button.png" />
                        </button>
                      </div>
                    </div>
                    <div className="slider_whole_dot">
                        <a className="button frst_sld"  onClick={e => this.slider.slickGoTo(0)}>
                          <div className="sldr_dot"></div>
                          <div class="sldr_line"></div>
                          <div className="year_dot">2013</div>
                        </a>
                        <a className="button frst_sld"  onClick={e => this.slider.slickGoTo(1)}>
                          <div className="sldr_dot"></div>
                          <div class="sldr_line"></div>
                          <div className="year_dot">2014</div>
                        </a>
                        <a className="button frst_sld"  onClick={e => this.slider.slickGoTo(2)}>
                          <div className="sldr_dot"></div>
                          <div class="sldr_line"></div>
                          <div className="year_dot">2015</div>
                        </a>
                        <a className="button frst_sld"  onClick={e => this.slider.slickGoTo(3)}>
                          <div className="sldr_dot"></div>
                          <div class="sldr_line sldr_line_last"></div>
                          <div className="year_dot">2016</div>
                        </a>
                      </div>
                  </div>
          );
        }
    }
}
export default India;
