import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import {BarChart} from 'react-d3-components';
import Slider from "react-slick";
import * as d3 from "d3";
import {Bar} from 'react-chartjs-2';
//var ReactCSSTransitionGroup = require('react-addons-css-transition-group');

// Here we init the variables
let year = 0;
//This is the style var we are using
let yearStyle;
//This was being used to hold state data from the chart, but i'm not using it now, it's left in incase you want to use it
let stateData;
let statePosition;
// This is false by default i.e chart wont play auto
let playChart = true;


const data_1 = {
  labels: ['2013-2014', '2014-2015', '2015-2016','2016-2017'],
  datasets: [
    {
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-2.6],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [2.4],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [4.7],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'GDP Growth Rate (World)',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [2.9],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
    {
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 0,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [127.1],
      backgroundColor:[
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
        'rgba(200,200, 200, 0)',
      ],
    },
  ]
}

const sngl_bar = value => {
   return 'rgb(68, 114, 196)';
};

class South_Africa extends Component {
    constructor(props) {
        super(props);
        this.play = this.play.bind(this);
        this.pause = this.pause.bind(this);
        this.state = { width: window.innerWidth, chartData: data_1}; // responsive mobile

      }

      play() {
        this.slider.slickPlay();
      }
      pause() {
        this.slider.slickPause();
      }

      // responsive mobile Start
        componentWillMount() {
          window.addEventListener('resize', this.handleWindowSizeChange);
        }

        // make sure to remove the listener
        // when the component is not mounted anymore
        componentWillUnmount() {
          window.removeEventListener('resize', this.handleWindowSizeChange);
        }

        handleWindowSizeChange = () => {
          this.setState({ width: window.innerWidth });
        };

  animation(data, datasetPosition) {
    const oldState = this.state.chartData;

    let datasets = oldState.datasets;
    datasets[datasetPosition] = data;
    const newState = Object.assign({}, oldState, {
      datasets: datasets
    })

    this.setState({
      chartData: newState
    })
  }

  updateColumn(data, datasetPosition) {
    const oldState = this.state.chartData;

    let datasets = oldState.datasets;
    datasets[datasetPosition] = data;
    const newState = Object.assign({}, oldState, {
      datasets: datasets
    })

    this.setState({
      chartData: newState
    })
  }

  componentDidMount() {
    setTimeout(() => this.animation({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-2.6],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 500)

    setTimeout(() => this.animation({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [2.4],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 1000)
        //
    setTimeout(() => this.animation({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      // data: [1, 0, -0.5, 1.7],
      data: [4.7],
      backgroundColor:[
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE',
        '#BEBEBE'
      ],
    }, 2), 1500)

    setTimeout(() => this.animation({
      label: 'GDP Growth Rate (world)',
      backgroundColor: '#DCDCDC',
      borderColor: '#DCDCDC',
      borderWidth: 1,
      hoverBackgroundColor: '#DCDCDC',
      hoverBorderColor: '#DCDCDC',
      // data: [2.9, 2.9, 2.5, 3.2],
      data: [2.9],
      backgroundColor:[
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC',
        '#DCDCDC'
      ],
    }, 3), 2000)

    setTimeout(() => this.animation({
      label: 'Growth Rate of SMEs Using PayPal',
      backgroundColor: '#4472c4',
      borderColor: '#4472c4',
      borderWidth: 1,
      hoverBackgroundColor: '#4472c4',
      hoverBorderColor: '#4472c4',
      // data: [6.2, 14.9, 32.8, 36.9],
      data: [127.1],
      backgroundColor:[
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4',
        '#4472c4'
      ],
    }, 4), 2500)


    setTimeout(() => this.updateColumn({
      label: 'Growth Rate of Total Offline Exports',
      backgroundColor: '#C8C8C8',
      borderColor: '#C8C8C8',
      borderWidth: 1,
      hoverBackgroundColor: '#C8C8C8',
      hoverBorderColor: '#C8C8C8',
      data: [-2.6, -13.3],
      backgroundColor:[
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8",
        "#C8C8C8"
      ],
    }, 0), 3000)

    setTimeout(() => this.updateColumn({
          label: 'Growth Rate of PayPal Mirror Basket of Offline Exports',
          backgroundColor: '#A9A9A9',
          borderColor: '#A9A9A9',
          borderWidth: 1,
          hoverBackgroundColor: '#A9A9A9',
          hoverBorderColor: '#A9A9A9',
          data: [2.4, -7.2,],
          backgroundColor:[
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9",
            "#A9A9A9"
          ],
        }, 1), 3500)
        //
    setTimeout(() => this.updateColumn({
      label: 'GDP Growth Rate (Latin America & Caribbean)',
      backgroundColor: '#BEBEBE',
      borderColor:'#BEBEBE',
      borderWidth: 1,
      hoverBackgroundColor:'#BEBEBE',
      hoverBorderColor:'#BEBEBE',
      data: [4.7, 3.1],
      backgroundColor:[
        '#BEBEBE',
