				year = year + 1;
				this.alterStyle(212);
				currentIndex = 3;
	      break;
				}

	      case 4:
				if(mapPlay == true){
				year = 0;
				this.alterStyle(286);
				currentIndex = 4;
			}
	    }
			if(mapPlay == true){
				if(year !== 4){
					setTimeout(this.executeMapYear, this.padTime);
				}
			this.slider.slickGoTo(currentIndex)
				}
		}


